	memberId,
	memberPassword,
	memberNickName,
	memberPostCode,
	memberAddress,
	memberDetailAddress,
	memberEmail,
	memberBirthDay
)
VALUES
(
	#{memberId}
	, #{memberPassword}
	, #{memberNickName}
	, #{memberPostCode}
	, #{memberAddress}
	, #{memberDetailAddress}
	, #{memberEmail}
	, #{memberBirthDay}
)

mapper.update:
update spring5_member
set
	membername = #{membername}
	, email = #{email}
	, phone = #{phone}
	, address = #{address}
	
		, memberpw = #{memberpw}
	
where
	memberid = #{memberid}
