mapper.insert:
INSERT INTO MEMBERS
(
	memberId,
	memberPassword,
	memberNickName,
	memberPostCode,
	memberAddress,
	memberDetailAddress,
	memberEmail,
	memberBirthDay
)
VALUES
(
	#{memberId}
	, #{memberPassword}
	, #{memberNickName}
	, #{memberPostCode}
	, #{memberAddress}
	, #{memberDetailAddress}
	, #{memberEmail}
	, #{memberBirthDay}
)

mapper.update:
UPDATE
	members
SET
	memberNickName = #{memberNickName}
	, memberEmail = #{memberEmail}
	, memberAddress = #{memberAddress}
	, memberBirthDay = #{memberBirthDay}


	, memberImageOriginal = #{memberImageOriginal}
	, memberImageSaved = #{memberImageSaved}

WHERE
	memberId = #{memberId}
