mapper.insert:
INSERT INTO MEMBERS
(
	memberId,
	memberPassword,
	memberNickName,
	memberPostCode,
	memberAddress,
	memberDetailAddress,
	memberEmail,
	memberBirthDay
)
VALUES
(
	#{memberId}
	, #{memberPassword}
	, #{memberNickName}
	, #{memberPostCode}
	, #{memberAddress}
	, #{memberDetailAddress}
	, #{memberEmail}
	, #{memberBirthDay}
)

mapper.update:
UPDATE
	members
SET
	memberPassword = #{memberPassword}
	, memberNickName = #{memberNickName}
	, memberEmail = #{memberEmail}
	, memberAddress = #{memberAddress}


	, boardImageOriginal = #{boardImageOriginal}
	, boardImageSaved = #{boardImageSaved}

WHERE
	memberId = #{memberId}
