mapper.insert:
insert into BOARD (
	boardNumber
	, memberId
	, boardTitle
	, memberNickName
	, boardContents
	, boardCategory
	)
values (
	boardNumber_seq.nextval
	, #{memberId}
	, #{boardTitle}
	, #{memberNickName}
	, #{boardContents}
	, #{boardCategory}
	)

mapper.select:
select boardNumber , memberId , boardTitle , memberNickName , to_char(boardInputDate, 'YYYY-MM-DD') boardInputDate
, boardHits , boardLiked , boardReport , boardContents , boardCategory , boardImageOriginal , boardImageSaved from Board
where boardNumber = #{boardNumber}
