mapper.insert:
insert into BOARD (
boardNumber
, memberId
, boardTitle
, memberNickName
,
boardContents
, boardCategory

	, boardImageOriginal
	, boardImageSaved

)
values (
boardNumber_seq.nextval
, #{memberId}
, #{boardTitle}
,
#{memberNickName}
, #{boardContents}
, #{boardCategory}

	, #{boardImageOriginal}
	, #{boardImageSaved}

)

mapper.update:
insert into boardLikedData
(
boardLikedNumber,
boardNumber,
memberId
)
values (
boardLikedNumber_seq.nextval
, #{boardNumber}
, #{memberId}
)

update
Board
set
boardTitle = #{boardTitle}
, boardContents =
#{boardContents}
,
boardCategory = #{boardCategory}

	, boardImageOriginal = #{boardImageOriginal}
	,
	boardImageSaved = #{boardImageSaved}

where
boardNumber = #{boardNumber}
and memberId = #{memberId}

mapper.delete:
delete from
Board
where
boardNumber = #{boardNumber}
and memberId = #{memberId}

mapper.insert:
